mapper.select:
select rcg.customerId customerId, rcg.gatewaySerialNumber gatewaySerialNumber, rcg.isOnwer isOnwer, rcg.gatewayName gatewayName, rcg.createdDate createdDate, rcg.modifiedDate modifiedDate, rcg.id relCustomerGatewayId, gaw.address address, gaw.port port, gaw.parameter parameter, gaw.ip ip, gaw.createdDate createdDate, gaw.modifiedDate modifiedDate, gaw.id gatewayId from rel_customer_gateway rcg LEFT JOIN gateway gaw on rcg.gatewaySerialNumber = gaw.serialNumber WHERE rcg.isDeleted = 0 and gaw.isDeleted = 0
and rcg.id =#{relCustomerGatewayId}
and rcg.customerId =#{customerId}
and rcg.gatewaySerialNumber =#{gatewaySerialNumber}
and rcg.isOnwer =#{isOnwer}
and rcg.gatewayName =#{gatewayName}
and rcg.createdDate =#{createdDate}
and rcg.modifiedDate =#{modifiedDate}
ORDER BY rcg.id ASC
